import React from 'react'
import { useSelectedBrokerName } from '../../hooks/useSelectedBroker'
import { useQueues } from '../../hooks/useQueues'

import {
  Card,
  CardHeader,
  CardTitle,
  CardBody,
  Alert,
  AlertGroup,
  Label
} from '@patternfly/react-core'

import {
  CheckCircleIcon,
  ExclamationTriangleIcon,
  ExclamationCircleIcon
} from '@patternfly/react-icons'

export const BrokerAlerts: React.FC = () => {
  const brokerName = useSelectedBrokerName()

  if (!brokerName) {
    return (
      <Card isFlat isCompact>
        <CardBody>
          <Alert variant="danger" title="No broker selected" isInline />
        </CardBody>
      </Card>
    )
  }

  // SWR: carica tutte le queue
  const { data: queues = [], error, isLoading } = useQueues(brokerName)

  // Calcolo alert (funzione pura)
  const computeAlerts = () => {
    if (!queues || queues.length === 0) return []

    let totalLag = 0
    let totalInflight = 0
    let consumers = 0
    let memorySum = 0

    queues.forEach(q => {
      const inflight = q.stats.inflight ?? 0
      const size = q.size ?? 0

      totalLag += size - inflight
      totalInflight += inflight
      consumers += q.consumers
      memorySum += q.memory.percent
    })

    const avgMemory = memorySum / queues.length

    const alerts: string[] = []

    if (avgMemory > 80) alerts.push(`High average memory: ${avgMemory.toFixed(1)}%`)
    if (totalLag > 50000) alerts.push(`High global lag: ${totalLag}`)
    if (totalInflight > 10000) alerts.push(`Too many inflight messages: ${totalInflight}`)
    if (consumers === 0) alerts.push(`No active consumers in the broker`)

    return alerts
  }

  const alerts = computeAlerts()

  const severity =
    alerts.length === 0
      ? 'green'
      : alerts.length < 3
      ? 'orange'
      : 'red'

  const severityLabel =
    severity === 'green'
      ? 'Healthy'
      : severity === 'orange'
      ? 'Warnings'
      : 'Critical'

  const severityIcon =
    severity === 'green'
      ? <CheckCircleIcon />
      : severity === 'orange'
      ? <ExclamationTriangleIcon />
      : <ExclamationCircleIcon />

  return (
    <Card isFlat isCompact>
      <CardHeader>
        <CardTitle>Broker Alerts</CardTitle>
        <Label color={severity} icon={severityIcon} style={{ marginLeft: 'auto' }}>
          {severityLabel}
        </Label>
      </CardHeader>

      <CardBody>

        {isLoading && (
          <Alert variant="info" title="Loading alerts…" isInline />
        )}

        {error && (
          <Alert variant="danger" title="Failed to load broker data" isInline />
        )}

        {!isLoading && !error && alerts.length === 0 && (
          <Alert variant="success" title="No global alerts" isInline />
        )}

        {!isLoading && !error && alerts.length > 0 && (
          <AlertGroup isLiveRegion>
            {alerts.map((a, i) => (
              <Alert key={i} variant="danger" title={a} isInline />
            ))}
          </AlertGroup>
        )}

      </CardBody>
    </Card>
  )
}

import React, { useMemo } from 'react'
import {
  Chart,
  ChartLine,
  ChartAxis,
  ChartGroup
} from '@patternfly/react-charts'

interface Props {
  data: number[]
  color?: string
}

export const Sparkline: React.FC<Props> = ({ data, color = '#00bcd4' }) => {
  // Pre-elaborazione sicura
  const chartData = useMemo(
    () => data.map((v, i) => ({ x: i, y: v })),
    [data]
  )

  // Se non ci sono dati, linea piatta
  const safe = chartData.length > 0 ? chartData : [{ x: 0, y: 0 }]

  // Dominio Y dinamico
  let yMin = Math.min(...safe.map(p => p.y))
  let yMax = Math.max(...safe.map(p => p.y))

  // Evita dominio piatto
  if (yMin === yMax) {
    const pad = Math.max(1, Math.abs(yMin) * 0.1)
    yMin -= pad
    yMax += pad
  }

  return (
    <div style={{ width: '100%', height: 40 }}>
      <Chart
        height={40}
        padding={{ top: 0, bottom: 0, left: 0, right: 0 }}
        domain={{ y: [yMin, yMax] }}
        animate={{ duration: 300, easing: 'cubicOut' as const }}
      >
        <ChartGroup>
          <ChartLine
            data={safe}
            style={{
              data: {
                stroke: color,
                strokeWidth: 1.5
              }
            }}
          />
        </ChartGroup>

        {/* Assi nascosti */}
        <ChartAxis
          style={{
            axis: { stroke: 'none' },
            ticks: { stroke: 'none' },
            tickLabels: { fill: 'none' }
          }}
        />
        <ChartAxis
          dependentAxis
          style={{
            axis: { stroke: 'none' },
            ticks: { stroke: 'none' },
            tickLabels: { fill: 'none' }
          }}
        />
      </Chart>
    </div>
  )
}

import React, { useMemo } from 'react'
import {
  Card,
  CardBody,
  Title,
  Alert,
  AlertGroup,
  AlertVariant,
  Flex,
  FlexItem
} from '@patternfly/react-core'

import {
  ExclamationTriangleIcon,
  ExclamationCircleIcon,
  CheckCircleIcon
} from '@patternfly/react-icons'

import { Queue } from '../../types/domain'

interface Props {
  queue: Queue
  history: Queue[]
}

type AlertItem = {
  variant: AlertVariant
  title: string
  icon: React.ReactNode
  severity: number   // 0 = critical, 1 = warning, 2 = info, 3 = success
}

export const QueueAlerts: React.FC<Props> = ({ queue, history }) => {

  const alerts = useMemo(() => computeQueueAlerts(queue, history), [queue, history])

  return (
    <Card isFlat isCompact>
      <CardBody>
        <Title headingLevel="h4">Alerts</Title>

        {alerts.length === 0 && (
          <Alert
            variant="success"
            title="No alerts"
            isInline
            customIcon={<CheckCircleIcon />}
          />
        )}

        {alerts.length > 0 && (
          <AlertGroup isLiveRegion>
            {alerts.map((a, i) => (
              <Alert
                key={i}
                variant={a.variant}
                isInline
                customIcon={a.icon}
                title={
                  <Flex spaceItems={{ default: 'spaceItemsSm' }}>
                    <FlexItem>{a.icon}</FlexItem>
                    <FlexItem>{a.title}</FlexItem>
                  </Flex>
                }
              />
            ))}
          </AlertGroup>
        )}
      </CardBody>
    </Card>
  )
}

function computeQueueAlerts(queue: Queue, history: Queue[]): AlertItem[] {
  const alerts: AlertItem[] = []
  const lag = queue.size - queue.stats.inflight

  const push = (
    variant: AlertVariant,
    title: string,
    icon: React.ReactNode,
    severity: number
  ) => alerts.push({ variant, title, icon, severity })

  /* MEMORY */
  if (queue.memory.percent > 80) {
    push(
      AlertVariant.danger,
      `High memory usage: ${queue.memory.percent}%`,
      <ExclamationCircleIcon />,
      0
    )
  }

  /* NO CONSUMERS */
  if (queue.consumers === 0) {
    push(
      AlertVariant.warning,
      `No active consumers`,
      <ExclamationTriangleIcon />,
      1
    )
  }

  /* LAG */
  if (lag > 10000) {
    push(
      AlertVariant.danger,
      `High consumer lag: ${lag.toLocaleString()} messages`,
      <ExclamationCircleIcon />,
      0
    )
  }

  /* INFLIGHT */
  if (queue.stats.inflight > 500) {
    push(
      AlertVariant.warning,
      `Too many inflight messages: ${queue.stats.inflight.toLocaleString()}`,
      <ExclamationTriangleIcon />,
      1
    )
  }

  /* QUEUE SIZE */
  if (queue.size > 10000) {
    push(
      AlertVariant.warning,
      `Queue backlog growing: ${queue.size.toLocaleString()} messages`,
      <ExclamationTriangleIcon />,
      1
    )
  }

  /* DISPATCH STALL */
  if (history.length > 2) {
    const latest = history.at(-1)
    const prev = history.at(-2)

    if (latest && prev) {
      const enqueueDelta = latest.stats.enqueue - prev.stats.enqueue
      const dispatchDelta = latest.stats.dequeue - prev.stats.dequeue

      if (enqueueDelta > 0 && dispatchDelta === 0) {
        push(
          AlertVariant.danger,
          `Messages enqueued but not dispatched`,
          <ExclamationCircleIcon />,
          0
        )
      }
    }
  }

  /* ORDER BY SEVERITY */
  alerts.sort((a, b) => a.severity - b.severity)

  return alerts
}

import React from 'react'
import {
  Card,
  CardBody,
  Accordion,
  AccordionItem,
  AccordionToggle,
  AccordionContent,
  DescriptionList,
  DescriptionListGroup,
  DescriptionListTerm,
  DescriptionListDescription,
  Label
} from '@patternfly/react-core'

import {
  CheckCircleIcon,
  TimesCircleIcon,
  ExclamationTriangleIcon
} from '@patternfly/react-icons'

import { Queue } from '../../types/domain'

interface Props {
  queue: Queue
}

export const QueueAttributes: React.FC<Props> = ({ queue }) => {
  const [expanded, setExpanded] = React.useState<string>('runtime')

  const onToggle = (id: string) => {
    setExpanded(prev => (prev === id ? '' : id))
  }

  const formatValue = (value: any, label: string) => {
    if (value === undefined || value === null) return '—'

    // Booleani con icone PF5
    if (typeof value === 'boolean') {
      return value ? (
        <Label color="green" icon={<CheckCircleIcon />}>Yes</Label>
      ) : (
        <Label color="red" icon={<TimesCircleIcon />}>No</Label>
      )
    }

    // Numeri con formattazione
    if (typeof value === 'number') {
      // Highlight dinamico per valori critici
      if (label.toLowerCase().includes('percent') && value > 80) {
        return (
          <Label color="red" icon={<ExclamationTriangleIcon />}>
            {value.toLocaleString()}%
          </Label>
        )
      }

      return value.toLocaleString()
    }

    return String(value)
  }

  const Row = ({ label, value }: { label: string; value: any }) => (
    <DescriptionListGroup>
      <DescriptionListTerm>{label}</DescriptionListTerm>
      <DescriptionListDescription>
        {formatValue(value, label)}
      </DescriptionListDescription>
    </DescriptionListGroup>
  )

  const Section = ({
    id,
    title,
    rows
  }: {
    id: string
    title: string
    rows: { label: string; value: any }[]
  }) => (
    <AccordionItem>
      <AccordionToggle
        onClick={() => onToggle(id)}
        isExpanded={expanded === id}
        id={id}
      >
        {title}
      </AccordionToggle>

      <AccordionContent isHidden={expanded !== id}>
        <DescriptionList isHorizontal>
          {rows.map((r, i) => (
            <Row key={i} label={r.label} value={r.value} />
          ))}
        </DescriptionList>
      </AccordionContent>
    </AccordionItem>
  )

  const sections = [
    {
      id: 'runtime',
      title: 'Runtime',
      rows: [
        { label: 'Queue size', value: queue.size },
        { label: 'Enqueue count', value: queue.stats.enqueue },
        { label: 'Dequeue count', value: queue.stats.dequeue },
        { label: 'Expired', value: queue.stats.expired },
        { label: 'In flight', value: queue.stats.inflight },
        { label: 'Consumers', value: queue.consumers },
        { label: 'Producers', value: queue.producers },
        { label: 'Paused', value: queue.state.paused },
        { label: 'Stopped', value: queue.state.stopped },
        { label: 'DLQ', value: queue.state.dlq }
      ]
    },
    {
      id: 'memory',
      title: 'Memory',
      rows: [
        { label: 'Memory limit', value: queue.memory.limit },
        { label: 'Memory usage bytes', value: queue.memory.usageBytes },
        { label: 'Memory percent usage', value: queue.memory.percent }
      ]
    },
    {
      id: 'dlq',
      title: 'DLQ',
      rows: [
        { label: 'DLQ enabled', value: queue.state.dlq }
      ]
    },
    {
      id: 'misc',
      title: 'Misc',
      rows: [
        { label: 'MBean', value: queue.mbean },
        { label: 'Name', value: queue.name }
      ]
    }
  ]

  return (
    <Card isFlat isCompact>
      <CardBody>
        <Accordion asDefinitionList>
          {sections.map(s => (
            <Section key={s.id} id={s.id} title={s.title} rows={s.rows} />
          ))}
        </Accordion>
      </CardBody>
    </Card>
  )
}

import React, { useState } from 'react'
import {
  Card,
  CardBody,
  Pagination,
  CodeBlock,
  CodeBlockCode,
  Spinner,
  EmptyState,
  EmptyStateHeader,
  EmptyStateBody
} from '@patternfly/react-core'

import {
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td
} from '@patternfly/react-table'

import { Queue } from '../../types/domain'
import { useQueueMessages } from '../../hooks/useQueueMessages'

interface Props {
  queue: Queue
}

export const QueueBrowser: React.FC<Props> = ({ queue }) => {
  const [page, setPage] = useState(0)
  const pageSize = 20

  const {
    data: messages = [],
    isLoading,
    error,
  } = useQueueMessages(queue.mbean, page, pageSize)

  const onSetPage = (_evt: any, newPage: number) => {
    setPage(newPage - 1) // PatternFly pages are 1-based
  }

  return (
    <Card isFlat isCompact>
      <CardBody>

        {/* LOADING */}
        {isLoading && (
          <div style={{ padding: '2rem', textAlign: 'center' }}>
            <Spinner size="xl" />
          </div>
        )}

        {/* ERROR */}
        {error && (
          <EmptyState>
            <EmptyStateHeader
              titleText="Failed to load messages"
              headingLevel="h4"
            />
            <EmptyStateBody>
              Try refreshing or navigating to another page.
            </EmptyStateBody>
          </EmptyState>
        )}

        {/* EMPTY STATE */}
        {!isLoading && !error && messages.length === 0 && (
          <EmptyState>
            <EmptyStateHeader
              titleText="No messages in this page"
              headingLevel="h4"
            />
            <EmptyStateBody>
              Try navigating to another page or wait for new messages.
            </EmptyStateBody>
          </EmptyState>
        )}

        {/* TABLE */}
        {!isLoading && !error && messages.length > 0 && (
          <>
            <Table variant="compact">
              <Thead>
                <Tr>
                  <Th>ID</Th>
                  <Th>Timestamp</Th>
                  <Th>Body</Th>
                </Tr>
              </Thead>

              <Tbody>
                {messages.map((m, i) => (
                  <Tr key={i}>
                    <Td>{m.id}</Td>
                    <Td>{new Date(m.timestamp).toLocaleString()}</Td>
                    <Td>
                      <CodeBlock readOnly>
                        <CodeBlockCode>
                          {JSON.stringify(m.body, null, 2)}
                        </CodeBlockCode>
                      </CodeBlock>
                    </Td>
                  </Tr>
                ))}
              </Tbody>
            </Table>

            <Pagination
              itemCount={999999} // ActiveMQ non dà il totale
              perPage={pageSize}
              page={page + 1}
              onSetPage={onSetPage}
              isCompact
              style={{ marginTop: '1rem' }}
            />
          </>
        )}

      </CardBody>
    </Card>
  )
}

import React from 'react'
import {
  Card,
  CardBody,
  Title,
  DescriptionList,
  DescriptionListGroup,
  DescriptionListTerm,
  DescriptionListDescription,
  Label,
  Flex,
  FlexItem
} from '@patternfly/react-core'

import {
  ArrowUpIcon,
  ArrowDownIcon,
  MinusIcon,
  ExclamationTriangleIcon
} from '@patternfly/react-icons'

import { Queue } from '../../types/domain'

interface Props {
  queue: Queue
  history: Queue[]
}

export const QueueConsumers: React.FC<Props> = ({ queue, history }) => {
  const latest = queue
  const prev = history.length >= 2 ? history.at(-2)! : null

  /* ────────────────────────────────────────────────
     DISPATCH RATE (msg/sec)
     ──────────────────────────────────────────────── */

  const dispatchRate = prev
    ? (latest.stats.dequeue - prev.stats.dequeue) / 2
    : 0

  const dispatchTrend =
    !prev
      ? 'flat'
      : dispatchRate > 0
      ? 'up'
      : dispatchRate < 0
      ? 'down'
      : 'flat'

  const trendIcon =
    dispatchTrend === 'up' ? (
      <ArrowUpIcon color="var(--pf-v5-global--success-color--100)" />
    ) : dispatchTrend === 'down' ? (
      <ArrowDownIcon color="var(--pf-v5-global--danger-color--100)" />
    ) : (
      <MinusIcon color="var(--pf-v5-global--palette--black-500)" />
    )

  /* ────────────────────────────────────────────────
     PREFETCH VALUES
     ──────────────────────────────────────────────── */

  const prefetchValues =
    latest.subscriptions?.map(s => s.flow.prefetchSize).filter(Boolean) ?? []

  /* ────────────────────────────────────────────────
     HIGHLIGHT DINAMICO
     ──────────────────────────────────────────────── */

  const inflightLabel =
    latest.stats.inflight > 500 ? (
      <Label color="orange" icon={<ExclamationTriangleIcon />}>
        {latest.stats.inflight.toLocaleString()}
      </Label>
    ) : (
      latest.stats.inflight.toLocaleString()
    )

  /* ──────────────────────────────────────────────── */

  return (
    <Card isFlat isCompact>
      <CardBody>
        <Title headingLevel="h4">Consumers</Title>

        <DescriptionList isHorizontal>

          {/* ACTIVE CONSUMERS */}
          <DescriptionListGroup>
            <DescriptionListTerm>Active Consumers</DescriptionListTerm>
            <DescriptionListDescription>
              {latest.consumers.toLocaleString()}
            </DescriptionListDescription>
          </DescriptionListGroup>

          {/* INFLIGHT */}
          <DescriptionListGroup>
            <DescriptionListTerm>Inflight</DescriptionListTerm>
            <DescriptionListDescription>
              {inflightLabel}
            </DescriptionListDescription>
          </DescriptionListGroup>

          {/* DISPATCH RATE */}
          <DescriptionListGroup>
            <DescriptionListTerm>Dispatch Rate</DescriptionListTerm>
            <DescriptionListDescription>
              <Flex spaceItems={{ default: 'spaceItemsSm' }}>
                <FlexItem>{dispatchRate.toFixed(1)} msg/s</FlexItem>
                <FlexItem>{trendIcon}</FlexItem>
              </Flex>
            </DescriptionListDescription>
          </DescriptionListGroup>

          {/* PREFETCH */}
          {prefetchValues.length > 0 && (
            <DescriptionListGroup>
              <DescriptionListTerm>Prefetch</DescriptionListTerm>
              <DescriptionListDescription>
                {prefetchValues.join(', ')}
              </DescriptionListDescription>
            </DescriptionListGroup>
          )}

        </DescriptionList>
      </CardBody>
    </Card>
  )
}

import React from 'react'
import {
  Card,
  CardBody,
  Title,
  DescriptionList,
  DescriptionListGroup,
  DescriptionListTerm,
  DescriptionListDescription,
  Label
} from '@patternfly/react-core'

import {
  CheckCircleIcon,
  TimesCircleIcon,
  ExclamationTriangleIcon
} from '@patternfly/react-icons'

import { Queue } from '../../types/domain'

interface Props {
  queue: Queue
}

export const QueueDLQ: React.FC<Props> = ({ queue }) => {

  /* ────────────────────────────────────────────────
     VALUE FORMATTER
     ──────────────────────────────────────────────── */

  const formatValue = (value: any, label: string) => {
    if (value === undefined || value === null) return '—'

    // Booleani con icone PF5
    if (typeof value === 'boolean') {
      return value ? (
        <Label color="green" icon={<CheckCircleIcon />}>Yes</Label>
      ) : (
        <Label color="red" icon={<TimesCircleIcon />}>No</Label>
      )
    }

    // Numeri con highlight dinamico
    if (typeof value === 'number') {
      if (label.toLowerCase().includes('expired') && value > 1000) {
        return (
          <Label color="orange" icon={<ExclamationTriangleIcon />}>
            {value.toLocaleString()}
          </Label>
        )
      }

      if (label.toLowerCase().includes('redelivered') && value > 1000) {
        return (
          <Label color="red" icon={<ExclamationTriangleIcon />}>
            {value.toLocaleString()}
          </Label>
        )
      }

      return value.toLocaleString()
    }

    return String(value)
  }

  /* ────────────────────────────────────────────────
     DECLARATIVE ROWS
     ──────────────────────────────────────────────── */

  const rows = [
    { label: 'Is DLQ', value: queue.state.dlq },
    { label: 'Expired Messages', value: queue.stats.expired },
    { label: 'Redelivered Messages', value: queue.stats.redelivered }
  ]

  /* ──────────────────────────────────────────────── */

  return (
    <Card isFlat isCompact>
      <CardBody>
        <Title headingLevel="h4">DLQ / Redelivery</Title>

        <DescriptionList isHorizontal>
          {rows.map((r, i) => (
            <DescriptionListGroup key={i}>
              <DescriptionListTerm>{r.label}</DescriptionListTerm>
              <DescriptionListDescription>
                {formatValue(r.value, r.label)}
              </DescriptionListDescription>
            </DescriptionListGroup>
          ))}
        </DescriptionList>
      </CardBody>
    </Card>
  )
}

import React, { useMemo } from 'react'
import {
  Card,
  CardHeader,
  CardTitle,
  CardBody,
  Grid,
  GridItem,
  Flex,
  FlexItem,
  Label
} from '@patternfly/react-core'

import {
  Chart,
  ChartLine,
  ChartVoronoiContainer
} from '@patternfly/react-charts'

import {
  ArrowUpIcon,
  ArrowDownIcon,
  MinusIcon
} from '@patternfly/react-icons'

import { Queue } from '../../types/domain'

interface Props {
  history: Queue[]
}

export const QueueMetricsOverview: React.FC<Props> = ({ history }) => {

  const data = useMemo(() => {
    return history.map(h => ({
      time: new Date(h.timestamp).toLocaleTimeString(),
      size: h.size,
      enqueue: h.stats.enqueue,
      dequeue: h.stats.dequeue,
      inflight: h.stats.inflight,
      memory: h.memory.percent,
    }))
  }, [history])

  if (data.length < 2) {
    return (
      <Card isFlat className="pf-v5-u-mb-lg">
        <CardHeader>
          <CardTitle>Metrics Overview</CardTitle>
        </CardHeader>
        <CardBody>
          <p className="pf-v5-u-color-200">Not enough data to display metrics.</p>
        </CardBody>
      </Card>
    )
  }

  const last = data.at(-1)!
  const prev = data.at(-2)!

  const trendIcon = (current: number, previous: number) => {
    if (current > previous) {
      return <ArrowUpIcon color="var(--pf-v5-global--success-color--100)" />
    }
    if (current < previous) {
      return <ArrowDownIcon color="var(--pf-v5-global--danger-color--100)" />
    }
    return <MinusIcon color="var(--pf-v5-global--palette--black-500)" />
  }

  const spark = (values: number[], color: string, domain?: any) => (
    <Chart
      height={60}
      padding={{ top: 5, bottom: 5, left: 30, right: 10 }}
      containerComponent={<ChartVoronoiContainer labels={() => ''} />}
      domain={domain}
    >
      <ChartLine
        data={values.map((y, i) => ({ x: i, y }))}
        style={{ data: { stroke: color } }}
      />
    </Chart>
  )

  const metrics = [
    {
      label: 'Queue Size',
      value: last.size.toLocaleString(),
      trend: trendIcon(last.size, prev.size),
      spark: spark(data.map(d => d.size), 'var(--pf-v5-global--primary-color--100)')
    },
    {
      label: 'Enqueue',
      value: last.enqueue.toLocaleString(),
      trend: trendIcon(last.enqueue, prev.enqueue),
      spark: spark(data.map(d => d.enqueue), 'var(--pf-v5-global--success-color--100)')
