        <Modal
          title="Confirm deletion"
          isOpen={isOpen}
          onClose={() => setIsOpen(false)}
          actions={[
            <Button
              key="confirm"
              variant={ButtonVariant.danger}
              isDisabled={isDeleting}
              onClick={del}
            >
              {isDeleting ? <Spinner size="sm" /> : 'Yes, delete'}
            </Button>,
            <Button
              key="cancel"
              variant={ButtonVariant.secondary}
              isDisabled={isDeleting}
              onClick={() => setIsOpen(false)}
            >
              Cancel
            </Button>
          ]}
        >
